var pos= msg.payload.search("RCV");
if (pos==3){
    data= msg.payload.split(",");
    //data[0].substr(7);
    //msg.payload.msgid=data[1];
    msg.payload={
        eui : data[0].substring(7,20),
        msgId : parseInt(data[1]),
        version: data[2],
        rssi : data[3],
        snr : data[4],
        temperature : data[5],
        voltage : data [6]
        };
}
msg.pos=pos;
return msg;

// read the sensor list
var sensorList = flow.get("sensorList");
if (sensorList===undefined ) {
    sensorList=[];
    flow.set("sensorList", sensorList);
}
var index=-1;
var newSensor=false;

// search the sensor in the list
sensorList.forEach(function(item,i,array){
   if (item.eui==msg.payload.eui) {
       index=i;
   }
});

if (index===-1) {
    // sensor not found, add new sensor in the list
    if (sensorList.length>14) { 
        // List full, discard message
        return null;
    }
    newSensor=true;
    index=sensorList.length;
    sensorList.push({
        "eui":msg.payload.eui,
        "received":0,
        "missed":0,
        "PER":0,
        "index":index,
        "lastMsgId":msg.payload.msgId-1,
        "lost":false
        });

} 

// compute the sensor information
var sensor = sensorList[index];

if (sensor.lost===true){
    newSensor=true;
}

if (msg.payload.msgId<=sensor.lastMsgId)  {
// wrap or reset of the counter (8 bit)   
    sensor.lastMsgId=msg.payload.msgId-1;
}
if (msg.payload.msgId>sensor.lastMsgId+1) {
    // missed packet detected
    sensor.missed=sensor.missed+ (msg.payload.msgId-sensor.lastMsgId-1);
}
sensor.lastMsgId=msg.payload.msgId;
sensor.received=sensor.received+1;
sensor.rssi=msg.payload.rssi;
sensor.snr=msg.payload.snr;
sensor.temperature=msg.payload.temperature;
sensor.voltage=msg.payload.voltage;
sensor.PER=new Intl.NumberFormat('en-IN', { maximumSignificantDigits: 3 }).format(100*sensor.missed/(sensor.missed+sensor.received));
sensor.lost = false;//flow.set("sensorList", sensorList);


// build the message with data for the table
msg.topic=index;
msg.payload.PER=sensor.PER;
msg.payload.index=sensor.index;
msg.payload.received=sensor.received;
msg.payload.missed=sensor.missed;

if (newSensor===true) {
    // send the new sensor index to activate the chart.
    return [msg, { payload:index }];
}

return msg;

