    if (input==="Concentrator Reset") {
        flow.set("AT_Command",""); 
        msg.topic="resetok";
        msg.flow="ALL";
        return msg;

    } 
    if (input.includes("AT+RCV=")) {
        flow.set("AT_Command",""); 
        msg.topic="data";
        msg.flow="ALL";
        msg.payload=input;
        return msg;

    } 
    if (input.includes("AT+LOST=")) {
        flow.set("AT_Command",""); 
        msg.topic="lost";
        msg.flow="ALL";
        msg.payload=input;
        return msg;
    }
    if (input.includes("AT+MOD_OK=")) {
